    {
        $result = [
            'code' => $code,
            'question' => $question,
            'explanation' => $explanation,
            'difficulty' => $difficulty,
            'answers' => array_map(static function (array $answer): array {
                return [
                    'text' => $answer[0],
                    'correct' => $answer[1],
                ];
            }, $answers),
        ];
        
        if ($imageUrl !== null) {
            $result['image_url'] = $imageUrl;
        }
        
        return $result;
    }

